import { useState, useEffect } from 'react';

const Warning = (data) => {
  
  const [errors, setErrors] = useState({});
  const intergratedValue=data.map((value)=>{
    setErrors({...errors,[value.name]:""})
    return({...value,"error":errors[value.name]});
  })
  const [isSubmitting, setIsSubmitting] = useState(false);
  const  Validate=(value,name,validationStandard,required)=>{
    if(!value&&required){
      setErrors({...errors,[name]:" is required"})
      return
    }else{
      if(validationStandard==="name"){
          var notName=value.match(/\d+/g);
          if(notName!==null){
            setErrors({errors,[name]:" can not include a number"})
          }else{
            setErrors({...errors,[name]:false})
          }
      }else if(validationStandard==="phone"){
          var notPhone=value.match(/\D/g)
          if(notPhone!==null){
            setErrors({errors,[name]:" can not include character"})
          }else{
            setErrors({...errors,[name]:false})
          }
      }else{
        setErrors({...errors,[name]:false})
      }
    }
  };
  /*const addUser=(user)=>{
    fetch(SERVER_URL+'api/doctors',{
      method:'POST',
      headers:{'Content-Type':'application/json'},
      body: JSON.stringify(user)
    }).then(response=>{
      if(response.ok){
        alert('all ok')
      }else{
        alert('something is worng ')
      }
    }).catch(err=>alert(err))

  }*/
  const handleSubmit = (event) => {
    if (event) event.preventDefault();
      intergratedValue[0]["error"]="fucked";
     // addUser(values);

    };
  const handler = (event,type,required,validationType) => {
    if(event){
      if(type==='onBlur'){
        event.persist();
        Validate(event.target.value,event.target.name,validationType,required);
      }else if(type==="onChange"){
        event.persist()
        Validate(event.target.value,event.target.name,validationType,required);
      }
    }
    
  };

  return {
    handler,
    handleSubmit,
    intergratedValue
  }
};


export default Warning
