import React, { useState } from 'react';
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import './addDoctor.css'
function AddDoctor() {

  function TextInput({ type = 'text', label }) {
    const [value, setValue] = useState('');

    function handleChange(e) {
      setValue(e.target.value);
    }

    return (
   
        <div className="input-container">
          <input type={type} value={value} onChange={handleChange} />
          <label className={value && 'filled'} >
            {label}
          </label>

        </div>
  
    );
  }
  function DateInput({ type = 'date', label }) {
    const [value, setValue] = useState('');

    function handleChange(e) {
      setValue(e.target.value);
    }

    return (
   
        <div className="input-container">
          <input type={type} value={value} onChange={handleChange} />
          <label className={value && 'filled'} >
            {label}
          </label>

        </div>
  
    );
  }

  return (
    <div>
      <Header pageTitle={"Doctor"} pageholder={PaymentResponse.pageHandler} />
      <span id="add">ADD DOCTOR</span>

      <h3 className="title">Personal Information</h3>

      <form>
        <TextInput label="First name" />
        <TextInput label="Last name" />
        <TextInput label="CellPhone1" />
        <TextInput label="CellPhone2(optional)" />
        <TextInput label="City" />
        <TextInput label="Sub City" />
        <TextInput label="Woreda" />
        <DateInput label="Date of Birth"/>
        <TextInput label="Birth Place" />
      </form>


      <Footer pageTitle={"Doctor"} />
    </div>
  );


}







export default AddDoctor;
