import { useState, useEffect } from 'react';

const Warning = (callback) => {

  let values={
    'email':'sah',
    'f_name':'asd',
    'gender':'sad',
    'l_name':'asd',
    'password':'asd',
    'role':'sad',
    'subcity':'ads',
    'woreda':'07',
    'date_employed':'2023-04-05',
    'qualification':'sadd',
    'speciality':'asd',
    'cell_phone1':'sad',
    'cell_phone2':'sad',
    'city':'sda',
    'dob':'2023-04-05'
  };
  const [SERVER_URL,setServerUrl]=useState("http://localhost:8080/");
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const  Validate=(value,name)=>{
    if(!value){
      setErrors({...errors,[name]:true})
      return
    }else{
      let temp=errors;
      temp[name]=false;
      setErrors(temp);
    }
  };
  const addUser=(user)=>{
    fetch(SERVER_URL+'api/user',{
      method:'POST',
      headers:{'Content-Type':'application/json'},
      body: JSON.stringify(user)
    }).then(response=>{
      if(response.ok){
        alert('all ok')
      }else{
        alert('something is worng ')
      }
    })

  }

  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmitting) {
      callback();
    }
  }, [errors]);

  const handleSubmit = (event) => {
    if (event) event.preventDefault();
      setIsSubmitting(true);
      addUser(values,'user');

    };
  const handleInputChange = (event) => {
    event.persist();
    //Validate(event.target.value,event.target.name);
    //values={ ...values, [event.target.name]: event.target.value };
  };

  return {
    handleInputChange,
    handleSubmit,
    values,
    errors,
  }
};


export default Warning
