import React, { useState } from 'react';
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import Warning from '../Login/warning';
import {FaExclamationTriangle} from 'react-icons/fa';
import {FaEnvelope} from 'react-icons/fa';

import './addDoctor.css'
function AddDoctor() {
  function Validate(values) {
    let errors = {};
    if (!values.first) {
      errors.first = 'First Name is required';
    } 
    if (!values.lastName) {
      errors.lastName = 'lastName is required';
    } 
    if (!values.cellphone) {
      errors.cellphone = 'Cell Phone value is required';
    } 
    if (!values.city) {
      errors.city = 'City is required';
    } 
    if (!values.subCity) {
      errors.subCity = 'Sub City is required';
    } 
    if (!values.woreda) {
      errors.woreda = 'Woreda is required';
    } 
    if (!values.birthPlace) {
      errors.birthPlace = 'Birth Place is required';
    } 
    if (!values.dob) {
      errors.dob = 'Date Of Birth is required';
    } 
    if (!values.email) {
      errors.email = 'Email address is required';
    } else if (!/\S+@\S+\.\S+/.test(values.email)) {
      errors.email = 'Email address is invalid';
    }
    if (!values.password) {
      errors.password = 'Password is required';
    } else if (values.password.length < 8) {
      errors.password = 'Password must be 8 or more characters';
    }
    if (!values.university) {
      errors.university = 'University Name is required';
    } 
    if (!values.cgpa) {
      errors.cgpa = 'CGPA is required';
    } 
    if (!values.graduationYear) {
      errors.graduationYear = 'Year of graduation is required';
    } 
    if (!values.language) {
      errors.language = 'Language is required';
    } 
    return errors;
  }; 
  const {
    errors,
    handleInputChange,
    handleSubmit,
  } = Warning(add, Validate);
  function add() {
    console.log('No errors, submit callback called!');
  }

  

  return (
    <div>
      <Header pageTitle={"Doctor"} pageholder={PaymentResponse.pageHandler} />
      <span id="add">ADD DOCTOR</span>

      <h3 className="title">Personal Information</h3>

   <form  onSubmit={handleSubmit}>
       
       <div className="input-container">
        <input type="text"  id='first-name'  onChange={handleInputChange}/>
          <label htmlFor='first-name'>
            First Name
          </label>
          {errors.first && (
                    <p id="warning"><FaExclamationTriangle/>{errors.first}</p>
