import React from "react";
import FloatingLabelInput from 'react-floating-label-input';

import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import './addDoctor.css'
function AddDoctor() {
 
    const inputs = document.getElementById('input');
    const input = document.getElementById('float-label');

  inputs.addEventListener("blur", (event) => {
    if (event.target.value) {
      input.classList.add("active");
    } else {
      input.classList.remove("active");
    }
  });

    return(
        <div>
            <Header pageTitle={"Doctor"} pageholder={PaymentResponse.pageHandler}/>
<span id="add">ADD DOCTOR</span>

    <h3 className="title">Personal Information</h3>
    <form className="form">
        <div id="float-label">
    <input type='text' id="input" />
    <label htmlFor="input">First Name</label>
    </div>
    <FloatingLabelInput type="email" id="floatingInput" floatingClassName="mb-3" floatingLabel="Email address" placeholder="name@example.com" />

    </form>


<Footer pageTitle={"Doctor"}/>
        </div>
         );
    
}
export default AddDoctor;
