import React from 'react'
import { useState } from 'react';
import logo from "../image/logo_with_s.png";
import './Login.css'
import Footer from '../Footer/Footer'
import { FaEyeSlash} from "react-icons/fa"
import { FaEye } from 'react-icons/fa';
import Warning from './warning';
import { render } from 'react-dom';

function Login(props){
  const [passwordShow, setPasswordShow]=useState(false);
  const [SERVER_URL,setServerUrl]=useState("http://localhost:8080/");
  const [Authorization,setAuth]=useState(false);
  const [user,setUser]=useState({username:'',password:''});
  const [isActive, setIsActive] = useState(false);
  const [isActive2, setIsActive2] = useState(false);
//input warning
function Warning(){
 
  return (
    <Register />
  );

}

const validEmailRegex = RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);
const validateForm = (errors) => {
let valid = true;
Object.values(errors).forEach(
  (val) => val.length > 0 && (valid = false)
);
return valid;
}

class Register extends Component {
constructor(props) {
  super(props);
  this.state = {
    email: null,
    password: null,
    errors: {
      email: '',
      password: '',
    }
  };
}

handleChange = (event) => {
  event.preventDefault();
  const { name, value } = event.target;
  let errors = this.state.errors;

  switch (name) {
    
    case 'email': 
      errors.email = 
        validEmailRegex.test(value)
          ? ''
          : 'Email is not valid!';
      break;
    case 'password': 
      errors.password = 
        value.length < 8
          ? 'Password must be 8 characters long!'
          : '';
      break;
    default:
      break;
  }

  this.setState({errors, [name]: value});
}

handleSubmit = (event) => {
  event.preventDefault();
  if(validateForm(this.state.errors)) {
    console.info('Valid Form')
  }else{
    console.error('Invalid Form')
  }
}

}  //showPassword
  const showPassword =()=>{
    setPasswordShow(!passwordShow);
  }
  //change text 
  const handleTextChange=(event)=> {
    setUser({...user,
    [event.target.name]:event.target.value});

    if(event.target.name==='username'){
      if (event.target.value !== '') {  
        setIsActive(true);
      } else {
        setIsActive(false);
      }
    }else {
      if (event.target.value !== '') {
        setIsActive2(true);
      } else {
        setIsActive2(false);
      }
    }
    
  }
  const login=()=>{
    fetch(SERVER_URL + 'login', {
      method: 'POST',
      headers: { 'Content-Type':'application/json' },
      body: JSON.stringify(user)
      })
      .then(res => {
      const jwtToken = res.headers.get
      ('Authorization');
      if (jwtToken !== null) {
      sessionStorage.setItem("jwt", jwtToken);
      setAuth(true);
      }
      })
      .catch(err => console.error(err));
  }

if(Authorization){
  return(
    <div>allowed</div>
  );

}else{
  
  const {errors} = this.state;
  return(
    
    <section className='loginPage'>
          
          <section className='LoginCenter'>
          <div className='loginImgholder'>
            <img src={logo} alt="company logo" width='130' id= 'img' height='50' />
            </div>
          <div className='loginContent'>
            <form id='login-form'>
          <div id="float-label">
            <input type="text" value={user.username} onChange={()=>{this.handleTextChange;this.handleChange}} name='username' noValidate/><br/>
            {errors.email.length > 0 && 
                <span className='error'>{errors.email}</span>}
            <label className={ isActive ? "Active" : ""} htmlFor="email" >Email</label>
          </div>
          <div id="float-label">
            <input type={passwordShow? "text":"password" }value={user.password} onChange={handleTextChange}name='password'/>
            {passwordShow?< FaEye onClick={showPassword} alt="show password icon" width='50' id= 'showpassword' height='50'/>:
            < FaEyeSlash onClick={showPassword} alt="show password icon" width='50' id= 'showpassword' height='50'/>}
            
          <label className={ isActive2 ? "Active" : ""} htmlFor="password" >Password</label>
         </div>
        
          <button id='button' onClick={login}>Login</button> </form>
          <div id='loginFootage'>
            <input type="checkbox" value="Remember me"/>Remember Me 
            <a href='./login.js'>Forget password?</a>     
          </div></div>
          
          </section>
          
         
          <Footer pageTitle={"Login"} pageHandler={props.pageHandler}/>
    </section>
);

}
}

    export default Login
