import React from "react";
import {FaExclamationTriangle} from 'react-icons/fa';
import { FaEnvelope } from "react-icons/fa";
import './addUser.css'
import { useState } from "react";

function SingleInputContainer(props){
    props.handler(null,props.name);
    const [labelFocus,setLabelFocus]= useState(props.to+"Label");
    const [inputValue,setInputValue]=useState("")
    const onChangeHandler=(e)=>{
        setInputValue(e.target.value);
        props.handler(e,'onChange');
        if(!e.target.value){
            setLabelFocus(props.to+"Label");
        }else{
            setLabelFocus("filled");
        }
    }
    
return(
    <div className='singleContainer'>
    <input type={props.type} className= {props.name+"Input"}  name={props.name}  onBlur={(e)=>props.handler(e,'onBlur')} 
    onChange={onChangeHandler} value={inputValue}/>
    <label htmlFor={props.name}className={labelFocus}>{props.label}</label>
    {props.name==='email'&&<FaEnvelope style={{marginLeft:'-2vw'}}/>}
    {props.error && (<p className="warning"><FaExclamationTriangle/>{props.label+props.error['message']}</p>)}
    </div>
);
}
export default SingleInputContainer;
