import { useState, useEffect } from 'react';

const Warning = (callback) => {

  let values={
    "password": "$2a$10$8cjz47bjbR4Mn8GMg9IZx.vyjhLXR/SKKMSZ9.mP9vpMu0ssKi8GW",
    "role": "admin",
    "gender": "male",
    "speciality":"teaching",
    "qualification":"grade"
  };
  const [SERVER_URL,setServerUrl]=useState("http://localhost:8080/");
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const  Validate=(value,name,validationStandard,required)=>{
    if(!value&&required){
      setErrors({...errors,[name]:" is required"})
      return
    }else{
      if(validationStandard==="name"){
          var notName=value.match(/\d+/g);
          if(notName!==null){
            setErrors({errors,[name]:" can not include a number"})
          }else{
            setErrors({...errors,[name]:false})
          }
      }else if(validationStandard==="phone"){
          var notPhone=value.match(/\D/g)
          if(notPhone!==null){
            setErrors({errors,[name]:" can not include character"})
          }else{
            setErrors({...errors,[name]:false})
          }
      }
    }
  };
  const addUser=(user)=>{
    fetch(SERVER_URL+'api/doctors',{
      method:'POST',
      headers:{'Content-Type':'application/json'},
      body: JSON.stringify(user)
    }).then(response=>{
      if(response.ok){
        alert('all ok')
      }else{
        alert('something is worng ')
      }
    }).catch(err=>alert(err))

  }

  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmitting) {
      callback();
    }
  }, [errors]);

  const handleSubmit = (event) => {
    if (event) event.preventDefault();
      setIsSubmitting(true);
      addUser(values);

    };
  const handler = (event,type,required,validationType) => {
    if(event){
      if(type==='onBlur'){
        event.persist();
        Validate(event.target.value,event.target.name,validationType,required);
      }else if(type==="onChange"){
        event.persist()
        Validate(event.target.value,event.target.name,validationType,required);
        values={ ...values, [event.target.name]: event.target.value };
      }
    }else{
      values={...values,[type]:""}
    }
    
  };

  return {
    handler,
    handleSubmit,
    values,
    errors,
  }
};


export default Warning
