import React from 'react';
import Warning from './warning';
import {FaExclamationTriangle} from 'react-icons/fa';
import {FaEnvelope} from 'react-icons/fa';
import  {GrFormClose} from 'react-icons/gr'
import './addDoctor.css'


function AddDoctor(props) {
  const {values,errors,handleInputChange,handleSubmit} = Warning(add, Validate);
  const addDoctorInputValue=[
    {
      'name':'firstName',
      'type':'text',
      'handler':handleInputChange,
      'error':errors.firstName,
      'label':'First Name'
    },{
      'name':'lastName',
      'type':'text',
      'handler':handleInputChange,
      'error':errors.lastName,
      'label':'Last Name'
    }
  ];
  const addDoctorInput=addDoctorInputValue.map((value)=>{
    return(<SingleInputContainer name={value.name} type={value.type} handler={value.handler}
     label={value.label}/>);
  })
  function add() {
    alert("added");
  }
  return (
    <section className='add_User'>
      <div className='add_user_Title'>
        <h2>{props.pageTitle}</h2>
        <div className='addDoctorClose' onClick={()=>{props.close(false,"home")}}>
          <GrFormClose size={30}/>
          </div></div>
      <form  onSubmit={handleSubmit}>
        <div className='formInput'>
        <div className='personal_Information'> 
          <h3 className="title">Personal Information</h3>
            <div className='fullName'>
              {addDoctorInput[0]}
          </div>
          <div className='phoneNumber'>
            <div className='singleContainer'>
              <input type="tel" className= 'warning'  name="cellphone" value={values.cellphone || ''} 
                onChange={handleInputChange} id='cellphone1' />
              <label htmlFor='cellphone1'>Cellphone1</label>
              {errors.cellphone && (<p className="warning"><FaExclamationTriangle/>{errors.cellphone}</p>)}
            </div>
            <div className='singleContainer'>
              <input type="tel"  id='cellphone2'/>
              <label htmlFor='cellphone2'>Cellphone2(optional)</label>
            </div>
          </div>
          <div className='address'>
            <div className='singleContainer'>
              <input type="text" className= 'warning' onChange={handleInputChange}  name="city" value={values.city || ''} 
              id='city'/>
              <label htmlFor='city'>City</label>
              {errors.city && (<p className="warning"><FaExclamationTriangle/>{errors.city}</p>)}
            </div>
            <div className='singleContainer'>
              <input type="text"  className= 'warning' onChange={handleInputChange}  name="subcity" value={values.subcity || ''}  
              id='sub-city'/>
