              <label htmlFor='woreda'>Woreda</label>
              {errors.woreda && (<p className="warning"><FaExclamationTriangle/>{errors.woreda}</p>)}
            </div>
          </div>
          <div className='birthInfo'>
            <div className='singleContainer'>
              <input type="text" className= 'warning' onChange={handleInputChange}  name="Bplace" value={values.birthPlace || ''} 
               id='Bplace'/>
