import { useState, useEffect } from 'react';

const Warning = (callback) => {

  let values={
    "password": "$2a$10$8cjz47bjbR4Mn8GMg9IZx.vyjhLXR/SKKMSZ9.mP9vpMu0ssKi8GW",
    "role": "admin",
    "gender": "male",
    "speciality":"teaching",
    "qualification":"grade"
  };
  const [SERVER_URL,setServerUrl]=useState("http://localhost:8080/");
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const  Validate=(value,name,validationStandard)=>{
    if(!value){
      setErrors({...errors,[name]:true})
      return
    }else{
      if(validationStandard==="name"){
          var matches=value.matche(/\d+/g);
          if(matches!==null){
            alert(value);
          }
      }
      let temp=errors;
      temp[name]=false;
      setErrors(temp);
    }
  };
  const addUser=(user)=>{
    fetch(SERVER_URL+'api/doctors',{
      method:'POST',
      headers:{'Content-Type':'application/json'},
      body: JSON.stringify(user)
    }).then(response=>{
      if(response.ok){
        alert('all ok')
      }else{
        alert('something is worng ')
      }
    }).catch(err=>alert(err))

  }

  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmitting) {
      callback();
    }
  }, [errors]);

  const handleSubmit = (event) => {
    if (event) event.preventDefault();
      setIsSubmitting(true);
      addUser(values);

    };
  const handleInputChange = (event) => {
    event.persist();
    Validate(event.target.value,event.target.name);
    values={ ...values, [event.target.name]: event.target.value };
  };

  return {
    handleInputChange,
    handleSubmit,
    values,
    errors,
  }
};


export default Warning
