import React from "react";
import Warning from "./warning";
function HCP(props){
    const {values,errors,handleInputChange,handleSubmit} = Warning(add);
    const addHcpInput=[
        {
            'name':'Name',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.name,
            'label':'Name'   
        },{
            'name':'cellPhone1',
            'type':'tel',
            'handler':handleInputChange,
            'error':errors.cellphone,
            'label':'Cell Phone 1'
          },{
            'name':'cellPhone2',
            'type':'tel',
            'handler':handleInputChange,
            'error':"",
            'label':'Cell Phone 2(optional)'
          },{
            'name':'city',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.city,
            'label':'City'
          },{
            'name':'subcity',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.subCity,
            'label':'Sub City'
          },{
            'name':'woreda',
            'type':'number',
            'handler':handleInputChange,
            'error':errors.woreda,
            'label':'Woreda'
          },
          
        {
            'name':'location',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.location,
            'label':'Location'   
        },
        {
            'name':'type',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.type,
            'label':'HCP Type'   
        },
        {
            'name':'speciality',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.speciality,
            'label':'Speciality'   
        },
        {
            'name':'owner',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.owner,
            'label':'Ownerr/Owners'   
        },
        {
            'name':'Fyear',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.name,
            'label':'Foundation Year'   
        }
    ]
    function add (){
        alert("add")
    }
    const addHcpInputValues=addHcpInputValues.map((value,index)=>{
        index=value.name
        return(<SingleInputContainer name={value.name} type={value.type} handler={value.handler}
        label={value.label} error={value.error}/>);
      })
    return(
        <form onSubmit={handleSubmit}>
<div className='fullName'>
              {addHcpInputValues[0]}
            </div>
            <div className='phoneNumber'>
              {addHcpInputValues[1]}{addHcpInputValues[2]}
            </div>
            <div className='address'>
              {addHcpInputValues[3]}{addHcpInputValues[4]}{addHcpInputValues[5]}
              </div>
            {/* </div>
            <hr className='middleHr'/>
          <div className='acadamy_information'>
            <h3 className="title">Acadamy Information</h3> */}
            <div>  {addHcpInputValues[6]}
              {addHcpInputValues[7]}
              {addHcpInputValues[8]}
              {addHcpInputValues[9]}
              
              {addHcpInputValues[10]}
              </div>
         
        </form>
    )

}

export default HCP;
