import React from "react";
import Warning from "./warning";
import SingleInputContainer from "./SingleInputContainer";
function Nurse(props){
    const {values,errors,handleInputChange,handleSubmit} = Warning(add);
    const addNurseInputValue=[
        {
          'name':'firstName',
          'type':'text',
          'handler':handleInputChange,
          'error':errors.firstName,
          'label':'First Name'
        },{
            'name':'last Name',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.lastName,
            'label':'Last Name'

        },
        {
            'name':'cellPhone1',
            'type':'tel',
            'handler':handleInputChange,
            'error':errors.cellphone,
            'label':'Cell Phone 1'
          },{
            'name':'cellPhone2',
            'type':'tel',
            'handler':handleInputChange,
            'error':"",
            'label':'Cell Phone 2(optional)'
          },{
            'name':'city',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.city,
            'label':'City'
          },{
            'name':'subcity',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.subCity,
            'label':'Sub City'
          },{
            'name':'woreda',
            'type':'number',
            'handler':handleInputChange,
            'error':errors.woreda,
            'label':'Woreda'
          },{
            'name':'birthPlace',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.birthPlace,
            'label':'Birth Place'
          },{
            'name':'dateOfBirth',
            'type':'Date',
            'handler':handleInputChange,
            'error':errors.dob,
            'label':'Date Of Birth'
          },{
            'name':'email',
            'type':'email',
            'handler':handleInputChange,
            'error':errors.email,
            'label':'Email'
          },{
            'name':'universityName',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.university,
            'label':'University Name'
          },{
            'name':'yearOfGraduaion',
            'type':'date',
            'handler':handleInputChange,
            'error':errors.graduationYear,
            'label':'Year Of Graduaion'
          },{
            'name':'CGPA',
            'type':'number',
            'handler':handleInputChange,
            'error':errors.cgpa,
            'label':'CGPA'
          },{
            'name':'language',
            'type':'text',
            'handler':handleInputChange,
            'error':errors.language,
            'label':'Language'
          },
    ];

    const addNurseInput=addNurseInputValue.map((value,index)=>{
        index=value.name
        return(<SingleInputContainer name={value.name} type={value.type} handler={value.handler}
        label={value.label}/>);
        })
    function add (){
        alert("add")
    }
    return(
        <form onSubmit={handleSubmit}>
            <div className='formInput'>
                {addNurseInput[0]}{addNurseInput[1]}
                {addNurseInput[2]}{addNurseInput[3]} {addNurseInput[4]}
                {addNurseInput[5]}
                {addNurseInput[6]}
                {addNurseInput[7]}
                {addNurseInput[8]}
                {addNurseInput[9]}
                {addNurseInput[10]} {addNurseInput[11]}
                {addNurseInput[12]} {addNurseInput[13]}
            </div>
          
        </form>
    )

}

export default Nurse;
